@implementation PV
static PV *sharedInstance = nil;
@synthesize language = _language;
@synthesize sectionTitles = _sectionTitles;
@synthesize centuries = _centuries;
@synthesize pageTitles = _pageTitles;
@synthesize content = _content;
@synthesize bubbles = _bubbles;


- (NSMutableArray *) content {
    if (!_content){
        _content = [[NSMutableArray alloc] init];
    }
return _content;
}
- (NSArray *) bubbles {
    if (!_bubbles){
        _bubbles = [[NSMutableArray alloc] init];
    }
    return _bubbles;
}
- (NSMutableArray *) sectionTitles {
    if (!_sectionTitles){
        _sectionTitles = [[NSMutableArray alloc] init];
    }
    return _sectionTitles;
}
- (NSMutableArray *) pageTitles {
    if (!_pageTitles){
        _pageTitles = [[NSMutableArray alloc] init];
    }
    return _pageTitles;
}
- (NSMutableArray *) centuries {
    if (!_centuries){
        _centuries = [[NSMutableArray alloc] init];
    }
    return _centuries;
}

- (NSString*) centuryOfSection:(int)century {
    if ([self.centuries count] >= century)
        return [self.centuries objectAtIndex:--century];
    else return @"";
}

- (NSString*) titleOfSection:(int)section {
    if ([self.pageTitles count] >= section){
        return [self.sectionTitles objectAtIndex:--section];}
    else return @"";
}
- (NSString*) titleOfPage:(int)page section:(int)section{
    //NSLog(@"content at page %d, section %d", page, section);
    NSMutableArray *pageTitles = [self.pageTitles objectAtIndex:--section];
    NSString *contentTemp = [pageTitles objectAtIndex:--page];
    //NSLog(@"Content %@", contentTemp);
    return contentTemp;
}

- (NSString*) contentAtPage:(int)page section:(int)section{
    //NSLog(@"content at page %d, section %d", page, section);
    NSLog(@"Lang: %@", self.language);
    NSMutableArray *sectionArray = [self.content objectAtIndex:--section];
    NSString *contentTemp = [sectionArray objectAtIndex:--page];
    //NSLog(@"Content %@", contentTemp);
    return contentTemp;
}

- (NSString*) bubbleContentForPainting:(int)painting bubble:(int)bubble{
    NSLog(@"bubble: paintid: %i, bubble:%i", painting, bubble);
    NSArray *bubblesArray = [self.bubbles objectAtIndex:--painting];
    NSLog(@"content :%@", [bubblesArray objectAtIndex:0]);
    NSString *contentTemp = [bubblesArray objectAtIndex:--bubble];
    return contentTemp;
}

- (NSMutableArray*) titlesofPages:(int)section {
    return [self.pageTitles objectAtIndex:--section];
}

- (PV*) initWithLanguage:(NSString*)userLanguage{
    self = [super init];
    [self setLanguage:userLanguage];
    
    if ([self.language isEqual:@"EN"]) [self loadEN];
    if ([self.language isEqual:@"ES"]) [self loadES];
    
    return self;
}


- (void) loadES {
    
    NSLog(@"Load ES");
    [self.content removeAllObjects];
    [self.pageTitles removeAllObjects];
    [self.sectionTitles removeAllObjects];
    [self.centuries removeAllObjects];
    
    // Section 1 - Prehistoria
    NSMutableArray *prehistoria = [[NSMutableArray alloc] init];
